stance.new("Sound", game.Workspace) sound.SoundId = "rbxassetid://18475341140" sound:Play() end) createButton("Sound Effect", UDim2.new(0.1, 0, 0.25, 0), function() local sound = Instance.new("Sound", game.Workspace) sound.SoundId = "rbxassetid://18477973439" sound:Play() end) createButton("Fire", UDim2.new(0.1, 0, 0.35, 0), function() for _, part in pairs(workspace:GetDescendants()) do if part:IsA("BasePart") then local fire = Instance.new("Fire", part) fire.Color = Color3.fromRGB(0, 255, 0) fire.Size = 20 end end end) createButton("Disco", UDim2.new(0.1, 0, 0.45, 0), function() while wait(0.1) do for _, part in pairs(workspace:GetDescendants()) do if part:IsA("BasePart") then part.Color = Color3.fromHSV(tick() % 5 / 5, 1, 1) end end game.Lighting.OutdoorAmbient = Color3.fromHSV(tick() % 5 / 5, 1, 1) end end) createButton("Kill All", UDim2.new(0.1, 0, 0.55, 0), function() for _, player in pairs(game.Players:GetPlayers()) do player.Character:BreakJoints() end end) createButton("Unanchor", UDim2.new(0.1, 0, 0.65, 0), function() for _, part in pairs(workspace:GetDescendants()) do if part:IsA("BasePart") then part.Anchored = false end end end) createButton("Hint", UDim2.new(0.1, 0, 0.75, 0), function() local hint = Instance.new("Hint", workspace) hint.Text = "hahaha k00lkidd81 has been returned" end) createButton("Message", UDim2.new(0.1, 0, 0.85, 0), function() local message = Instance.new("Message", workspace) message.Text = "hahaha k00lkidd81 has been returned" end) createButton("Shutdown", UDim2.new(0.1, 0, 0.95, 0), function() for _, player in pairs(game.Players:GetPlayers()) do player:Kick("haha k00lkidd81 has been returned\nError code: 81") end end) createButton("Chat", UDim2.new(0.1, 0, 1.05, 0), function() local chatService = game:GetService("Chat") for _, player in pairs(game.Players:GetPlayers()) do chatService:Chat(player.Character.Head, "team k00lkidd81 Join Today!", Enum.ChatColor.Blue) end end) createButton("Explode All", UDim2.new(0.1, 0, 1.15, 0), function() for _, player in pairs(game.Players:GetPlayers()) do local explosion = Instance.new("Explosion") explosion.Position = player.Character.HumanoidRootPart.Position explosion.Parent = workspace player.Character:BreakJoints() end end) createButton("Head", UDim2.new(0.1, 0, 1.25, 0), function() local userId = 5868909891 local headMesh = game.Players:GetHumanoidDescriptionFromUserId(userId).Head for _, player in pairs(game.Players:GetPlayers()) do local character = player.Character if character and character:FindFirstChild("Head") then character.Head:Destroy() local newHead = Instance.new("Part", character) newHead.Name = "Head" newHead.Size = Vector3.new(2, 1, 1) newHead.Position = character.Torso.Position + Vector3.new(0, 1, 0) local mesh = Instance.new("SpecialMesh", newHead) mesh.MeshId = headMesh newHead.Parent = character end end end) -- Bottom Label local bottomLabel = Instance.new("TextLabel") bottomLabel.Size = UDim2.new(1, 0, 0, 50) bottomLabel.Position = UDim2.new(0, 0, 1, -50) -- Position at the very bottom bottomLabel.BackgroundTransparency = 1 bottomLabel.Text = "made by k00lkidd81" bottomLabel.TextColor3 = Color3.fromRGB(255, 0, 0) bottomLabel.TextScaled = true bottomLabel.Font = Enum.Font.SourceSansBold bottomLabel.Parent = mainFrame -- Rainbow Text Effect spawn(function() while wait(0.1) do titleLabel.TextColor3 = Color3.fromHSV(tick() % 5 / 5, 1, 1) bottomLabel.TextColor3 = Color3.fromHSV(tick() % 5 / 5, 1, 1) end end)